   &amp;#125;

   &lt;span class=&#34;hljs-keyword&#34;&gt;size_t&lt;/span&gt; _get_root_index(&lt;span class=&#34;hljs-keyword&#34;&gt;size_t&lt;/span&gt; index) &amp;#123;
