   &lt;span class=&#34;hljs-keyword&#34;&gt;bool&lt;/span&gt; _is_empty() &amp;#123;
      &lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; size == &lt;span class=&#34;hljs-number&#34;&gt;0&lt;/span&gt;;
   &amp;#125;

   &lt;span class=&#34;hljs-keyword&#34;&gt;bool&lt;/span&gt; _is_full() &amp;#123;
      &lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; size == _max_size;
   &amp;#125;

   &lt;span class=&#34;hljs-keyword&#34;&gt;void&lt;/span&gt; _alloc() &amp;#123;
      T* tmp = _data;
      _data = &lt;span class=&#34;hljs-keyword&#34;&gt;new&lt;/span&gt; T[_max_size*&lt;span class=&#34;hljs-number&#34;&gt;2&lt;/span&gt;];
      &lt;span class=&#34;hljs-keyword&#34;&gt;for&lt;/span&gt; (&lt;span class=&#34;hljs-keyword&#34;&gt;size_t&lt;/span&gt; i = &lt;span class=&#34;hljs-number&#34;&gt;0&lt;/span&gt;; i &amp;lt; _max_size; i++)
      &amp;#123;
            _data[i] = tmp[i];
      &amp;#125;
